  public static int upOrDown(String str) {
    int floor = 0;
    for (int i = 0; i < str.length(); i++) {
      if (str.charAt(i) == '(') {
        floor++;
      } else if (str.charAt(i) == ')') {
        floor--;
      }
    }
    return floor;
  }

  public static int whenBasement(String str) {
    int step = 0;
    int floor = 0;
    while (floor >= 0) {
      if (str.charAt(step) == '(') {
        floor++;
      } else if (str.charAt(step) == ')') {
        floor--;
      }
      step++;
    }
    return step;
  }
